mapper.select:
select tc.base_no,tc.cno,tc.cname,tc.term,t.base_class,t.studycheck from traincourse tc,teaching t 

tc.cno=t.cno and base_no=#{base_no}

	and base_class=#{base_class}

select tc.base_no,tc.cno,tc.cname,tc.term,t.base_class,t.studycheck from traincourse tc,teaching t 

	tc.cno=t.cno and base_no=#{base_no}

		and base_class=#{base_class}


	limit ${page},${pagesize}
