mapper.update:
update student
set stu_name = #{stu_name},stu_sex = #{stu_sex},stu_class = #{stu_class},
	major = #{major},phone = #{phone},enter_year = #{enter_year},
	birthday = str_to_date(#{birthday},'%Y-%m-%d'),address = #{address}
where stuno = #{stuno}

